import React, { useState, useEffect, useCallback } from 'react';
import { 
  Box, 
  TextField, 
  Button, 
  Paper, 
  Container,
  Typography,
  Snackbar,
  Alert,
  CircularProgress,
  IconButton
} from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { useNavigate, useParams } from 'react-router-dom';
import axios from '../config/axios';
import { useAutoSave } from '../hooks/useAutoSave';
import { Letter, ApiResponse } from '../types';
import { EditorState, QuillConfig } from '../types/editor';
import LoadingState from '../components/LoadingState';

const QUILL_CONFIG: QuillConfig = {
  modules: {
    toolbar: [
      [{ header: [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{ list: 'ordered' }, { list: 'bullet' }],
      [{ align: [] }],
      ['link'],
      ['clean']
    ]
  },
  formats: [
    'header',
    'bold', 'italic', 'underline', 'strike',
    'list', 'bullet',
    'align',
    'link'
  ]
};

const Editor: React.FC = () => {
  const [state, setState] = useState<EditorState>({
    title: '',
    content: '',
    error: '',
    success: '',
    loading: false,
    saving: false
  });

  const navigate = useNavigate();
  const { id } = useParams<{ id: string }>();

  const fetchLetter = useCallback(async (): Promise<void> => {
    if (!id) return;

    try {
      setState(prev => ({ ...prev, loading: true }));
      const response = await axios.get<ApiResponse<Letter>>(`/api/letters/${id}`);
      setState(prev => ({
        ...prev,
        title: response.data.data.title,
        content: response.data.data.content,
        loading: false
      }));
    } catch (error) {
      setState(prev => ({ 
        ...prev, 
        error: 'Error loading letter',
        loading: false
      }));
    }
  }, [id]);

  useEffect(() => {
    if (id) {
      fetchLetter();
    }
  }, [id, fetchLetter]);

  const handleSave = async (): Promise<void> => {
    try {
      if (!state.title || !state.content) {
        setState(prev => ({ 
          ...prev, 
          error: 'Please fill in both title and content' 
        }));
        return;
      }

      setState(prev => ({ ...prev, saving: true }));
      const endpoint = id ? `/api/letters/${id}` : '/api/letters';
      const method = id ? 'put' : 'post';

      await axios[method]<ApiResponse<Letter>>(endpoint, {
        title: state.title,
        content: state.content
      });

      setState(prev => ({ 
        ...prev, 
        success: 'Letter saved successfully!',
        saving: false
      }));

      if (!id) {
        setTimeout(() => navigate('/'), 1500);
      }
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Error saving letter',
        saving: false
      }));
    }
  };

  const autoSave = useAutoSave(handleSave);

  const handleContentChange = (content: string): void => {
    setState(prev => ({ ...prev, content }));
    autoSave();
  };

  if (state.loading) {
    return <LoadingState message="Loading letter..." />;
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Paper sx={{ p: 3 }}>
        <Box sx={{ mb: 3 }}>
          <TextField
            fullWidth
            label="Title"
            value={state.title}
            onChange={(e) => setState(prev => ({ 
              ...prev, 
              title: e.target.value 
            }))}
            variant="outlined"
          />
        </Box>

        <ReactQuill
          theme="snow"
          value={state.content}
          onChange={handleContentChange}
          {...QUILL_CONFIG}
        />

        <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
          <Button
            variant="contained"
            onClick={handleSave}
            disabled={state.saving}
            startIcon={state.saving ? <CircularProgress size={20} /> : <SaveIcon />}
          >
            {state.saving ? 'Saving...' : 'Save'}
          </Button>
        </Box>
      </Paper>

      <Snackbar 
        open={!!state.error} 
        autoHideDuration={6000} 
        onClose={() => setState(prev => ({ ...prev, error: '' }))}
      >
        <Alert severity="error">{state.error}</Alert>
      </Snackbar>

      <Snackbar 
        open={!!state.success} 
        autoHideDuration={3000} 
        onClose={() => setState(prev => ({ ...prev, success: '' }))}
      >
        <Alert severity="success">{state.success}</Alert>
      </Snackbar>
    </Container>
  );
};

export default Editor;
